files.template:
provider "aws" { region = var.region }
resource "aws_s3_bucket" "site" { bucket = "my-static-site-demo" website { index_document = "index.html" } }
resource "aws_s3_bucket_policy" "public" { bucket = aws_s3_bucket.site.id policy = jsonencode({ Version:'2012-10-17', Statement:[{ Effect:'Allow', Principal:'*', Action:['s3:GetObject'], Resource:["\${aws_s3_bucket.site.arn}/*"] }] }) }
variable "region" { default = "us-east-1" }
