const { DataSource, EntitySchema } = require('typeorm');
const AppDataSource = new DataSource({ type: 'sqlite', database: 'app.db', entities: [new EntitySchema({ name: 'Note', columns: { id: { type: Number, primary: true, generated: true }, title: { type: String } } })], synchronize: true });
(async () => {
  await AppDataSource.initialize();
  const repo = AppDataSource.getRepository('Note');
  await repo.save({ title: 'hello' });
  console.log('Note count', await repo.count());
  await AppDataSource.destroy();
})();
