import Typesense from 'typesense';
const client = new Typesense.Client({ nodes: [{ host: 'localhost', port: 8108, protocol: 'http' }], apiKey: process.env.TYPESENSE_API_KEY || 'xyz' });
await client.collections().create({ name: process.env.TYPESENSE_COLLECTION || '{{collection}}', fields: [{ name: 'title', type: 'string' }] });
console.log('created');
