import Typesense from 'typesense';
const client = new Typesense.Client({ nodes: [{ host: (process.env.TS_NODE||'{{typesense_nodes}}').replace('http://','').replace('https://','').split(':')[0], port: Number((process.env.TS_NODE||'{{typesense_nodes}}').split(':')[2]||8108), protocol: (process.env.TS_NODE||'{{typesense_nodes}}').startsWith('https')?'https':'http' }], apiKey: process.env.TS_API_KEY || '{{api_key}}', connectionTimeoutSeconds: 2 });
try { await client.collections('docs').delete(); } catch {}
await client.collections().create({ name: 'docs', fields: [{ name: 'id', type: 'string' }, { name: 'text', type: 'string' }, { name: 'tag', type: 'string', facet: true } ] });
await client.documents('docs').create({ id: '1', text: 'hello', tag: 'a' });
await client.documents('docs').create({ id: '2', text: 'fluorite', tag: 'b' });
const res = await client.collections('docs').documents().search({ q: '*', query_by: 'text', filter_by: 'tag:=a' });
console.log(res.hits?.map(h=>h.document.text));
