import Typesense from 'typesense';
const c = new Typesense.Client({ nodes: [{ host:'localhost', port:8108, protocol:'http' }], apiKey: process.env.TYPESENSE_KEY });
const res = await c.multiSearch.perform({ searches: [{ q:'star', query_by:'title', collection:'movies' }] });
console.log(res.results?.length>=0);
