const Typesense = require('typesense');
const client = new Typesense.Client({ nodes: [{ host: '{{host}}'.replace('http://','').replace('https://',''), port: 8108, protocol: 'http' }], apiKey: '{{api_key}}' });
(async () => {
  await client.collections('movies').create({ name: 'movies', fields: [{ name: 'id', type: 'string' }, { name: 'title', type: 'string' }] }).catch(()=>{});
  await client.collections('movies').documents().create({ id: '1', title: 'Inception' });
  const r = await client.collections('movies').documents().search({ q: 'incep', query_by: 'title' });
  console.log('hits', r.hits?.length);
})();
