import Typesense from 'typesense';
const client = new Typesense.Client({ nodes: [{ host: (process.env.TS_NODE||'{{typesense_nodes}}').replace('http://','').replace('https://','').split(':')[0], port: Number((process.env.TS_NODE||'{{typesense_nodes}}').split(':')[2]||8108), protocol: (process.env.TS_NODE||'{{typesense_nodes}}').startsWith('https')?'https':'http' }], apiKey: process.env.TS_API_KEY || '{{api_key}}', connectionTimeoutSeconds: 2 });
try { await client.collections('docs').delete(); } catch {}
await client.collections().create({ name: 'docs', fields: [{ name: 'id', type: 'string' }, { name: 'text', type: 'string' }, { name: 'vec', type: 'float[]', num_dim: 3 } ] });
await client.documents('docs').create({ id: '1', text: 'hello world', vec: [0.1,0.2,0.3] });
await client.documents('docs').create({ id: '2', text: 'fluorite', vec: [0.2,0.1,0.25] });
const res = await client.multiSearch.perform({ searches: [{ collection: 'docs', q: '*', vector_query: 'vec:([0.1,0.2,0.3], k:2)' }] });
console.log(res.results?.[0]?.hits?.map(h=>h.document.text));
