import { Ratelimit } from '@upstash/ratelimit';
import { Redis } from '@upstash/redis';
const ratelimit = new Ratelimit({ redis: Redis.fromEnv(), limiter: Ratelimit.fixedWindow(5, '10 s') });
const id = process.env.RATE_ID || '{{identifier}}';
console.log(await ratelimit.limit(id));
