import { createClient, Provider, useQuery } from 'urql';
const client = createClient({ url: '/graphql' });
const Query = `query{ __typename }`;
function Hello(){ const [{ data }] = useQuery({ query: Query }); return <pre>{JSON.stringify(data)}</pre>; }
export default function App(){ return <Provider value={client}><Hello/></Provider>; }
