import { createAnthropic } from '@ai-sdk/anthropic';
import { generateText } from 'ai';
const anthropic = createAnthropic({ apiKey: process.env.ANTHROPIC_API_KEY });
const tools = { get_time: { description: 'Get current time', parameters: { type: 'object', properties: {} } } };
const res = await generateText({ model: anthropic('claude-3-5-sonnet-20240620'), tools, prompt: 'What time is it? Use the tool.' });
console.log(res.text);
