import { createOpenAI } from '@ai-sdk/openai';
import { generateText } from 'ai';
const openai = createOpenAI({ apiKey: process.env.OPENAI_API_KEY });
const tools = { get_time: { description: 'Get current time', parameters: { type: 'object', properties: {} } } };
const res = await generateText({ model: openai('gpt-4o-mini'), tools, prompt: 'What time is it? Use the tool.' });
console.log(res.text);
