import { VertexAI } from '@google-cloud/vertexai';
const v = new VertexAI({ project: process.env.GCP_PROJECT_ID || '{{gcp_project_id}}', location: process.env.GCP_LOCATION || '{{gcp_location}}' });
const model = v.getGenerativeModel({ model: process.env.VERTEX_EMBED_MODEL || '{{embedding_model}}' });
const res = await model.embedContent({ content: { parts: [{ text: 'hello world' }] } });
console.log(res?.embedding?.values?.length);
