import { VertexAI } from '@google-cloud/vertexai';
const vertex = new VertexAI({ project: process.env.GCP_PROJECT_ID || '{{gcp_project_id}}', location: process.env.GCP_LOCATION || '{{gcp_location}}' });
const model = vertex.getGenerativeModel({ model: process.env.VERTEX_MODEL || '{{vertex_model}}' });
const res = await model.generateContent({ contents: [{ role: 'user', parts: [{ text: 'Say hello' }] }] });
console.log(res.response?.candidates?.[0]?.content?.parts?.[0]?.text);
