import { createPinia, defineStore } from 'pinia';
export const pinia = createPinia();
export const useCounter = defineStore('counter', { state: ()=>({ n:0 }), actions: { inc(){ this.n++ } } });

import { createApp } from 'vue';
import { pinia, useCounter } from './store';
const App = { setup(){ const c = useCounter(); return { c }; }, template: '<button @click="c.inc()">{{ c.n }}</button>' };
createApp(App).use(pinia).mount('#app');
