import weaviate from 'weaviate-ts-client';
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
await client.batch.objectsBatcher()
  .withObject({ class: process.env.WEAVIATE_CLASS || '{{class_name}}', properties: { text: 'hello world' } })
  .withObject({ class: process.env.WEAVIATE_CLASS || '{{class_name}}', properties: { text: 'fluorite mcp' } })
  .do();
console.log('Batch upsert done');
