import weaviate from 'weaviate-ts-client';
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
const cls = { class: 'Document', vectorizer: 'none', properties: [{ name: 'text', dataType: ['text'] }] };
try {
  await client.schema.classCreator().withClass(cls).do();
  console.log('Created class Document');
} catch (e) {
  console.error('Error creating class', e.message);
}
