import weaviate from 'weaviate-ts-client';
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
for (const alpha of [0.2, 0.8]) {
  const res = await client.graphql.get().withClassName(process.env.WEAVIATE_CLASS || '{{class_name}}').withHybrid({ query: 'fluorite', alpha }).withLimit(2).do();
  console.log('alpha', alpha, JSON.stringify(res));
}
