import weaviate from 'weaviate-ts-client';
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
const cls = process.env.WEAVIATE_CLASS || '{{class_name}}';
const hyb = await client.graphql.get().withClassName(cls).withHybrid({ query: 'fluorite', alpha: 0.5 }).withLimit(2).do();
const bm = await client.graphql.get().withClassName(cls).withBm25({ query: 'fluorite' }).withLimit(2).do();
console.log('hybrid', JSON.stringify(hyb));
console.log('bm25', JSON.stringify(bm));
