import weaviate from 'weaviate-ts-client';
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
const res = await client.graphql.get().withClassName(process.env.WEAVIATE_CLASS || '{{class_name}}').withNearVector({ vector: [0.1, 0.2, 0.3] }).withLimit(3).do();
console.log(JSON.stringify(res, null, 2));
