import weaviate from 'weaviate-ts-client';
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
await client.tenants.creator().withClassName(process.env.WEAVIATE_CLASS || '{{class_name}}').withTenants([{ name: process.env.WEAVIATE_TENANT || '{{tenant_name}}' }]).do();
console.log('Tenant created');
