import weaviate from 'weaviate-ts-client';
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
await client.data.objectUpdater().withClassName(process.env.WEAVIATE_CLASS || '{{class_name}}').withId(process.env.WEAVIATE_ID || '{{object_id}}').withProperties({ text: 'updated' }).do();
console.log('Updated');
