const winston = require('winston');
const DailyRotateFile = require('winston-daily-rotate-file');
const transport = new DailyRotateFile({ filename: 'logs/app-%DATE%.log', datePattern: 'YYYY-MM-DD', zippedArchive: true, maxFiles: '14d' });
const logger = winston.createLogger({ level: 'info', transports: [new winston.transports.Console(), transport] });
logger.info('hello world');
logger.error('something went wrong');
