import WooCommerceRestApi from '@woocommerce/woocommerce-rest-api';
const api = new WooCommerceRestApi({ url: process.env.WC_URL, consumerKey: process.env.WC_KEY, consumerSecret: process.env.WC_SECRET, version: 'wc/v3' });
const r = await api.get('products', { per_page: 1 });
console.log(r.status);
