#!/usr/bin/env node
require('yargs')(process.argv.slice(2))
  .scriptName('{{app_name}}')
  .command('hello [name]', 'Print hello', y=> y.positional('name', { type: 'string', default: 'world' }), argv => {
    console.log(`hello ${argv.name}`);
  })
  .demandCommand()
  .help()
  .parse();
