import express from 'express';
import { z } from 'zod';
const app = express();
app.use(express.json());
const Body = z.object({ name: z.string() });
app.post('/echo', (req,res)=>{ const r = Body.safeParse(req.body); if (!r.success) return res.status(400).json({ error: r.error.message }); res.json(r.data); });
app.listen(Number('{{port}}'));
