import { z } from 'zod';
import { OpenAPIRegistry, OpenApiGeneratorV3 } from '@asteasolutions/zod-to-openapi';
const registry = new OpenAPIRegistry();
const User = registry.register('User', z.object({ id: z.number(), name: z.string() }));
const generator = new OpenApiGeneratorV3(registry.definitions);
console.log(JSON.stringify(generator.generateDocument({ openapi: '3.0.0', info: { title: 'API', version: '1.0.0' }, paths: {} }), null, 2));
