import express from 'express';
import { makeApi, z } from '@zodios/core';
import { createZodiosRouter } from '@zodios/express';
const api = makeApi([{ method: 'get', path: '/hello', response: z.object({ message: z.string() }) }]);
const router = createZodiosRouter(api);
router.get('/hello', (_req, res) => res.json({ message: 'hello' }));
const app = express();
app.use(router);
app.listen(3000);
