const express = require('express');
const crypto = require('crypto');
const app = express();
app.use(express.json({ type: '*/*' }));
app.post('/zoom', (req, res) => {
  const secret = process.env.ZOOM_WEBHOOK_SECRET || 'secret';
  const ts = req.headers['x-zm-request-timestamp'];
  const sig = req.headers['x-zm-signature'];
  const msg = `v0:${ts}:${JSON.stringify(req.body)}`;
  const mySig = 'v0=' + crypto.createHmac('sha256', secret).update(msg).digest('hex');
  res.status(sig === mySig ? 200 : 401).send('ok');
});
app.listen(3000, () => console.log('listening'));
